__NUXT_JSONP__("/works/trentes-recruit", (function(a){return {data:[{work:{id:"trentes-recruit",createdAt:a,updatedAt:"2022-01-08T12:25:13.269Z",publishedAt:a,revisedAt:a,title:"株式会社トロント・インダストリー \u002F RECRUIT SITE",category:[],release:"2014-11-30T15:00:00.000Z",thumbnail:{url:"https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F8f9b212cd3634f1a9343ab4f3011eaa6\u002Fd8587c218bc84838a51f9b03191f072d\u002Ftrentes_1-1.jpg",height:670,width:1080},overview:"株式会社トロント・インダストリー様のリクルートサイトを制作させていただきました。\n\n「IT」という言葉から受ける少し硬く感じるイメージを変えたいと思い、ITに興味がある女性にも興味を持ってもらえるような優しく親しみやすいデザインを考えました。\n\n目標に向かって進んでいくイメージを飛ぶモチーフに例え、暮らしの生活を支えるITインフラのイメージを伝えられる世界観を目指しました。",textlink1:"https:\u002F\u002Ftrentes.net\u002Frecruit\u002Findex.html"}}],fetch:{},mutations:void 0}}("2022-01-08T12:17:17.013Z")));